package br.com.elvisassis.sbootexp_secutiry.config;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import io.swagger.v3.oas.models.security.SecurityScheme;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class OpenApiConfig {    @Bean    public OpenAPI customOpenAPI() {        return new OpenAPI()                .info(new Info()                        .title("Spring Boot Experting Security API")                        .version("1.0")                        .description("Projeto de estudo com m√∫ltiplos AuthenticationProviders e controle de acesso via roles"))                .components(new io.swagger.v3.oas.models.Components()                        .addSecuritySchemes("basicAuth",                                new SecurityScheme()                                        .type(SecurityScheme.Type.HTTP)                                        .scheme("basic")));    }}