package br.com.elvisassis.sbootexp_secutiry.config;import br.com.elvisassis.sbootexp_secutiry.domain.security.CustomAuthentication;import br.com.elvisassis.sbootexp_secutiry.domain.security.UserIdentify;import org.springframework.security.authentication.AuthenticationProvider;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.security.core.Authentication;import org.springframework.security.core.AuthenticationException;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.stereotype.Component;import java.util.List;@Componentpublic class SenhaMasterAuthenticationProvider implements AuthenticationProvider {    @Override    public Authentication authenticate(Authentication authentication) throws AuthenticationException {        var login = authentication.getName();        var senha = authentication.getCredentials().toString();        String loginMaster = "master";        String senhaMaster = "@321";        if(loginMaster.equals(login) && senhaMaster.equals(senha)) {            UserIdentify userIdentify = new UserIdentify(                    "Sou Master",                    "Master",                    "master",                    List.of("ADMIN"));            return new CustomAuthentication(userIdentify);        }        return null;    }    @Override    public boolean supports(Class<?> authentication) {        return true;    }}