package br.com.elvisassis.sbootexp_secutiry.domain.dto;import br.com.elvisassis.sbootexp_secutiry.domain.entity.Permission;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class PermissionDTO {    private String id;    private String name;    public PermissionDTO(Permission permission) {        this.id = permission.getId();        this.name = permission.getName();    }    public Permission toEntity() {        return new Permission(name);    }}