package br.com.elvisassis.sbootexp_secutiry.domain.dto;import br.com.elvisassis.sbootexp_secutiry.domain.entity.User;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class UserDTO {    private String id;    private String login;    private String password;    private String name;    public UserDTO(User user) {        this.id = user.getId();        this.login = user.getLogin();        this.password = user.getPassword();        this.name = user.getName();    }    public User toEntity() {        return new User(login, password, name);    }}