package br.com.elvisassis.sbootexp_secutiry.domain.dto;import br.com.elvisassis.sbootexp_secutiry.domain.entity.User;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class UserDTO {    private String id;    private String username;    private String password;    private String fullname;    public UserDTO(User user) {        this.id = user.getId();        this.username = user.getUsername();        this.password = user.getPassword();        this.fullname = user.getFullName();    }    public User toEntity() {        return new User(username, password, fullname);    }}