package br.com.elvisassis.sbootexp_secutiry.domain.dto;import br.com.elvisassis.sbootexp_secutiry.domain.entity.UserGroup;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class UserGroupDTO {    private String id;    private UserDTO user;    private GroupDTO group;    public UserGroupDTO(UserGroup userGroup) {        this.user = new UserDTO(userGroup.getUser());        this.group = new GroupDTO(userGroup.getGroup());    }    public UserGroup toEntity() {        return new UserGroup(user.toEntity(), group.toEntity());    }}