package br.com.elvisassis.sbootexp_secutiry.domain.dto;import br.com.elvisassis.sbootexp_secutiry.domain.entity.UserPermission;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class UserPermissionDTO {    private UserDTO user;    private PermissionDTO permission;    public UserPermissionDTO(UserPermission userPermission) {        this.user = new UserDTO(userPermission.getUser());        this.permission = new PermissionDTO(userPermission.getPermission());    }}