package br.com.elvisassis.sbootexp_secutiry.domain.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Entity@Table(name = "'group'")@Data@AllArgsConstructor@NoArgsConstructorpublic class Group {  @Id  @GeneratedValue(strategy = GenerationType.UUID)  private String id;  private String name;  public Group (String name) {    this.name = name;  }}