package br.com.elvisassis.sbootexp_secutiry.domain.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.GenericGenerator;import org.springframework.security.core.GrantedAuthority;import java.util.UUID;@Entity@Table(name = "permission")@Data@AllArgsConstructor@NoArgsConstructorpublic class Permission implements GrantedAuthority {  @Id  @GeneratedValue(generator = "UUID")  @GenericGenerator(name = "UUID", strategy = "org.hibernate.id.UUIDGenerator")  private String id;  @Column(nullable = false, unique = true)  private String name;  @PrePersist  public void prePersist() {    if (this.id == null) {      this.id = UUID.randomUUID().toString();    }  }  public Permission(String name) {    this.name = name;  }  @Override  public String getAuthority() {    return this.name;  }}