package br.com.elvisassis.sbootexp_secutiry.domain.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;@Entity@Table(name = "'user'")@Data@AllArgsConstructor@NoArgsConstructorpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.UUID)    private String id;    private String login;    private String password;    private String name;    @Transient    private List<String> permissions;    public User(String login, String password, String name) {        this.login = login;        this.password = password;        this.name = name;    }}