package br.com.elvisassis.sbootexp_secutiry.domain.entity;import jakarta.persistence.*;import lombok.Data;import lombok.NoArgsConstructor;@Entity@Table(name = "user_group")@Data@NoArgsConstructorpublic class UserGroup {    public UserGroup(User user, Group group) {        this.user = user;        this.group = group;    }    @Id    @GeneratedValue(strategy = jakarta.persistence.GenerationType.UUID)    private String id;    @ManyToOne    @JoinColumn(name = "id_user")    private User user;    @ManyToOne    @JoinColumn(name = "id_group")    private Group group;}