package br.com.elvisassis.sbootexp_secutiry.domain.entity;import jakarta.persistence.*;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.annotations.GenericGenerator;@Entity@Table(name = "user_permission")@Data@NoArgsConstructorpublic class UserPermission {    public UserPermission(User user, Permission permission) {        this.user = user;        this.permission = permission;    }    @Id    @GeneratedValue(generator = "UUID")    @GenericGenerator(name = "UUID", strategy = "org.hibernate.id.UUIDGenerator")    private String id;    @ManyToOne    @JoinColumn(name = "id_user", nullable = false)    private User user;    @ManyToOne    @JoinColumn(name = "id_permission", nullable = false)    private Permission permission;}