package br.com.elvisassis.sbootexp_secutiry.domain.repository;import br.com.elvisassis.sbootexp_secutiry.domain.entity.Group;import br.com.elvisassis.sbootexp_secutiry.domain.entity.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;public interface GroupRepository extends JpaRepository<Group, String> {    @Query("SELECT g FROM Group g WHERE g.name IN :names")    List<Group> findAllByNames(@Param("names") List<String> names);}