package br.com.elvisassis.sbootexp_secutiry.domain.repository;import br.com.elvisassis.sbootexp_secutiry.domain.entity.Permission;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;public interface PermissionRepository extends JpaRepository<Permission, String> {    @Query("SELECT g FROM Permission g WHERE g.name IN :names")    List<Permission> findAllByNames(@Param("names") List<String> names);}