package br.com.elvisassis.sbootexp_secutiry.domain.repository;import br.com.elvisassis.sbootexp_secutiry.domain.entity.User;import br.com.elvisassis.sbootexp_secutiry.domain.entity.UserGroup;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;public interface UserGroupRepository extends JpaRepository<UserGroup, String> {    @Query("""            SELECT DISTINCT ug             FROM UserGroup ug             JOIN FETCH ug.user u             JOIN FETCH ug.group g             WHERE u.id = :id            """)    List<UserGroup> findByUserId(String id);    @Query("""            SELECT DISTINCT ug.group.name            FROM UserGroup ug             JOIN ug.user u             JOIN ug.group g             WHERE u = ?1            """)    List<String> findPermissionsByUser(User user);}