package br.com.elvisassis.sbootexp_secutiry.domain.security;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.ArrayList;import java.util.List;@Data@NoArgsConstructor@AllArgsConstructorpublic class UserIdentify {    private String id;    private String name;    private String login;    private List<String> permissions;    public List<String> getPermissions() {        if(permissions == null) permissions = new ArrayList<>();        return permissions;    }}