package br.com.elvisassis.sbootexp_secutiry.domain.service;import br.com.elvisassis.sbootexp_secutiry.domain.dto.GroupDTO;import br.com.elvisassis.sbootexp_secutiry.domain.repository.GroupRepository;import jakarta.transaction.Transactional;import org.springframework.stereotype.Service;import java.util.List;import java.util.stream.Collectors;@Servicepublic class GroupService {    private final GroupRepository repository;    public GroupService(GroupRepository repository) {        this.repository = repository;    }    @Transactional    public GroupDTO save(GroupDTO dto) {        var group = dto.toEntity();        var response = repository.save(group);        return new GroupDTO(response);    }    public List<GroupDTO> findAll() {        var groups = repository.findAll();        var response = groups.stream()                .map(GroupDTO::new)                .collect(Collectors.toList());        return response;    }}