package br.com.elvisassis.sbootexp_secutiry.domain.service;import br.com.elvisassis.sbootexp_secutiry.domain.dto.PermissionDTO;import br.com.elvisassis.sbootexp_secutiry.domain.repository.PermissionRepository;import jakarta.transaction.Transactional;import org.springframework.stereotype.Service;import java.util.List;import java.util.stream.Collectors;@Servicepublic class PermissionService {    private final PermissionRepository repository;    public PermissionService(PermissionRepository repository) {        this.repository = repository;    }    @Transactional    public PermissionDTO save(PermissionDTO dto) {        var permission = dto.toEntity();        var response = repository.save(permission);        return new PermissionDTO(response);    }    public List<PermissionDTO> findAll() {        var groups = repository.findAll();        var response = groups.stream()                .map(PermissionDTO::new)                .collect(Collectors.toList());        return response;    }}