package br.com.elvisassis.sbootexp_secutiry.mapper;import br.com.elvisassis.sbootexp_secutiry.domain.dto.AuthRequestDTO;import br.com.elvisassis.sbootexp_secutiry.domain.dto.UserDTO;import br.com.elvisassis.sbootexp_secutiry.domain.entity.User;public class UserMapper {    public static User userRequestDTOUser(UserDTO credentials, String passwordEncrypt) {        User user = new User();        user.setUsername(credentials.getUsername());        user.setPassword(passwordEncrypt);        user.setFullName(credentials.getFullname());        user.setAccountNonExpired(true);        user.setCredentialsNonExpired(true);        user.setAccountNonLocked(true);        user.setEnabled(true);        return user;    }    public static User authRequestDTOUser(AuthRequestDTO credentials, String passwordEncryty) {        User user = new User();        user.setUsername(credentials.getUsername());        user.setPassword(passwordEncryty);        user.setFullName(credentials.getFullname());        user.setAccountNonExpired(true);        user.setCredentialsNonExpired(true);        user.setAccountNonLocked(true);        user.setEnabled(true);        return user;    }    public static AuthRequestDTO userToAuthRequestDTO(User user) {        AuthRequestDTO dto = new AuthRequestDTO();        dto.setUsername(user.getUsername());        dto.setFullname(user.getFullName());        dto.setPassword(user.getPassword());        return dto;    }}